using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityStandardAssets.Characters.FirstPerson;  public class SoundTracker : MonoBehaviour {  	FirstPersonController player; 	public bool IsWater = false; 	public bool IsGrass = false;  	void Awake() 	{ 		player = FindObjectOfType<FirstPersonController> (); 	}  	void OnTriggerEnter(Collider other)  	{ 		if(IsWater) 			player.inWater = true;  		if (IsGrass) 			player.inGrass = true; 	}  	void OnTriggerExit(Collider other) 	{ 		player.inWater = false; 		player.inGrass = false; 	} }  